puts "Clearing db..."
Garden.destroy_all

puts "Creating gardens..."
italian = Garden.create!(
  name: 'Italian Garden',
  image_url: 'https://live.staticflickr.com/4315/35304357064_6fc0e99c91_b.jpg'
)

dutch = Garden.create!(
  name: 'Dutch Garden',
  image_url: 'https://upload.wikimedia.org/wikipedia/commons/4/49/Canal%2C_Westbury_Court_Garden_-_geograph.org.uk_-_1416966.jpg'
)

japanese = Garden.create!(
  name: 'Japanese Garden',
  image_url: 'https://cdn.mos.cms.futurecdn.net/iY2AFdXcXTRaiWDaFSNPSU.jpg'
)

puts "Creating plants..."
quadrifoglio = Plant.create!(
  name: 'Quadrifoglio',
  image_url: 'data:image/jpeg;base64,',
  garden: italian
)

willow = Plant.create!(
  name: 'Willow',
  image_url: 'https://cf.ltkcdn.net/garden/images/orig/283378-1600x1066-weeping-willow-tree.jpg',
  garden: dutch
)

wisteria = Plant.create!(
  name: 'Wisteria',
  image_url: 'https://www.trees.com/wp-content/uploads/products/raw/1000/Wisteria-Tree1.jpg',
  garden: dutch
)

tulip = Plant.create!(
  name: 'Tulip',
  image_url: 'https://cdn.webshopapp.com/shops/108044/files/111589040/650x750x1/tulipa-purple-rain.jpg',
  garden: dutch
)

cherry = Plant.create!(
  name: 'Cherry Blossom',
  image_url: 'https://www.trees.com/wp-content/uploads/2020/06/japanese-garden-plants.jpg',
  garden: japanese
)

camelia = Plant.create!(
  name: 'Camelia',
  image_url: 'https://www.trees.com/wp-content/uploads/2020/06/japanese-garden-plants.jpg',
  garden: japanese
)

ginseng = Plant.create!(
  name: 'Ginseng',
  image_url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRCBvtVWRhxHA-oEsmYO9dTODv6Pjmfi199Kg&usqp=CAU',
  garden: japanese
)

puts "Done!"
